tricks = {}

class Trick():

    def __init__(self, check_or_location, name, requirement_overrides, difficulty, child_or_adult='both', in_logic=False, reference=None):
        self.check = None
        self.location = None
        if check_or_location in check_list:
            self.check = check_or_location
        else:
            self.location = check_or_location
        self.name = name
        self.requirement_overrides = requirement_overrides
        self.difficulty = difficulty
        self.child_or_adult = child_or_adult
        self.reference = reference
        if check_or_location not in tricks:
            tricks[check_or_location] = []
        tricks[check_or_location].append(self)

    def __repr__(self):
        return self.name

